import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Element } from "react-scroll";

function App() {
  const [selectedProject, setSelectedProject] = useState(null);
  const projects = [
    {
      title: "Image Forgery Detection",
      summary: "Classifies images as real or forged using CNN.",
      description: "Built with Meso4 CNN to detect forged images. Tkinter GUI and real-time image upload with prediction.",
      image: "/assets/project1.png",
    },
    {
      title: "Network Intrusion Detection",
      summary: "Detects malicious traffic with deep learning.",
      description: "CNN + LSTM + XGBoost on CIC-IDS 2017 dataset with Flask UI to detect intrusion in unbalanced network data.",
      image: "/assets/project2.png",
    },
  ];

  const internships = [
    {
      title: "Cyber Security Intern",
      org: "Fortinet Security (AICTE)",
      duration: "July 202X â€“ Sept 202X",
      description: "Worked on SIEM tools, Penetration Testing, Firewall Configuration and built automation use-cases that improved response time by 40%."
    }
  ];

  const skills = [
    "Python", "Java", "C", "React", "TensorFlow", "Git", "HTML", "CSS", "Flask", "MySQL", "Linux", "Docker"
  ];

  const certifications = [
    "Ethical Hacking Essentials â€“ EC-Council",
    "Cyber Security Essentials â€“ Cisco Networking Academy",
    "DotNet Full Stack â€“ Wipro TalentNext",
    "SQL Injection Training â€“ EC-Council",
    "Intro to Cyber Security â€“ Infosys Springboard"
  ];

  return (
    <div className="bg-black text-white font-sans scroll-smooth">
      {/* Header */}
      <header className="fixed top-0 w-full z-50 bg-black bg-opacity-80 text-white shadow-md">
        <nav className="max-w-6xl mx-auto px-6 py-4 flex justify-between items-center">
          <div className="text-2xl font-bold text-blue-500">LS</div>
          <ul className="flex gap-6 text-sm">
            {['about', 'projects', 'internships', 'skills', 'certifications', 'contact'].map((section) => (
              <li key={section} className="cursor-pointer capitalize hover:text-blue-400">
                <a href={`#${section}`}>{section}</a>
              </li>
            ))}
          </ul>
        </nav>
      </header>

      {/* Hero / About Section */}
      <section id="about" className="h-screen flex items-center justify-center px-10 bg-cover bg-center" style={{ backgroundImage: `url('/assets/hero-bg.jpg')` }}>
        <div className="flex items-center max-w-6xl w-full gap-10">
          <img src="/assets/profile.png" alt="Lokesh" className="w-52 h-52 rounded-full border-4 border-blue-500 object-cover" />
          <div>
            <h1 className="text-5xl font-bold text-blue-400">Polagoni Lokesh</h1>
            <p className="text-gray-300 mt-4 text-lg">Cyber Security & Deep Learning Enthusiast</p>
          </div>
        </div>
      </section>

      {/* Projects */}
      <Element name="projects">
        <section id="projects" className="py-20 px-6 text-center bg-black">
          <h2 className="text-4xl font-bold mb-12">Projects</h2>
          <div className="grid md:grid-cols-2 gap-10 max-w-6xl mx-auto">
            {projects.map((project, i) => (
              <motion.div
                key={i}
                className="bg-gray-900 border border-gray-700 rounded-xl p-6 text-left shadow-md"
                whileHover={{ scale: 1.02 }}
              >
                <img src={project.image} alt={project.title} className="rounded-md mb-4 h-48 w-full object-cover" />
                <h3 className="text-xl font-semibold text-white">{project.title}</h3>
                <p className="text-gray-400 mt-1 mb-4">{project.summary}</p>
                <button
                  className="bg-blue-500 text-white px-4 py-2 rounded"
                  onClick={() => setSelectedProject(project)}
                >
                  View Project
                </button>
              </motion.div>
            ))}
          </div>
        </section>
      </Element>

      {/* Internships */}
      <section id="internships" className="py-20 px-6 bg-black text-center">
        <h2 className="text-4xl font-bold mb-12">Internships</h2>
        <div className="grid md:grid-cols-2 gap-6 max-w-5xl mx-auto text-left">
          {internships.map((intern, i) => (
            <div key={i} className="bg-gray-900 border border-gray-700 p-6 rounded-xl shadow-md">
              <h3 className="text-xl text-white font-bold">{intern.title}</h3>
              <p className="text-blue-400">{intern.org}</p>
              <p className="text-sm text-gray-400 mb-2">{intern.duration}</p>
              <p className="text-gray-300">{intern.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Skills */}
      <section id="skills" className="py-20 px-6 bg-black text-center">
        <h2 className="text-4xl font-bold mb-12">Skills</h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-6 max-w-4xl mx-auto">
          {skills.map((skill, i) => (
            <div key={i} className="bg-gray-900 text-white py-4 rounded shadow border border-gray-700">
              {skill}
            </div>
          ))}
        </div>
      </section>

      {/* Certifications */}
      <section id="certifications" className="py-20 px-6 bg-black text-center">
        <h2 className="text-4xl font-bold mb-12">Certifications</h2>
        <div className="grid md:grid-cols-2 gap-6 max-w-4xl mx-auto">
          {certifications.map((cert, i) => (
            <div key={i} className="border border-gray-700 p-4 rounded text-white bg-gray-900 shadow-md">
              {cert}
            </div>
          ))}
        </div>
      </section>

      {/* Contact */}
      <footer id="contact" className="py-10 text-center border-t border-gray-700 bg-cover bg-center" style={{ backgroundImage: `url('/assets/thank-you.png')` }}>
        <div className="bg-black bg-opacity-60 p-4">
          <h3 className="text-lg text-white">
            ðŸ“§ polagonilokesh739@gmail.com | ðŸ“± +91 7396177644 | ðŸ”— <a href="https://www.linkedin.com/in/polagoni-lokesh-749061248/" target="_blank" rel="noopener noreferrer">LinkedIn</a>
          </h3>
          <p className="text-gray-400 text-sm mt-2">Â© 2025 Polagoni Lokesh</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
