import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Element } from "react-scroll";

function App() {
  const [selectedProject, setSelectedProject] = useState(null);
  const [selectedInternship, setSelectedInternship] = useState(null);

  const projects = [
    {
      title: "Image Forgery Detection",
      summary: "Classifies images as real or forged using CNN.",
      description: "Built with Meso4 CNN to detect forged images. Tkinter GUI and real-time image upload with prediction.",
      image: "/assets/project1.png",
    },
    {
      title: "Network Intrusion Detection",
      summary: "Detects malicious traffic with deep learning.",
      description: "CNN + LSTM + XGBoost on CIC-IDS 2017 dataset with Flask UI to detect intrusion in unbalanced network data.",
      image: "/assets/project2.png",
    },
  ];

  const internships = [
    {
      title: "Cyber Security Intern",
      org: "Fortinet Security (AICTE)",
      duration: "July 202X – Sept 202X",
      description: "Worked on SIEM tools, Penetration Testing, Firewall Configuration and built automation use-cases that improved response time by 40%."
    }
  ];

  const skills = [
    { name: "Python", icon: "🐍" },
    { name: "Java", icon: "☕" },
    { name: "React", icon: "⚛️" },
    { name: "Linux", icon: "🐧" },
    { name: "Docker", icon: "🐳" },
    { name: "Git", icon: "🔧" },
    { name: "MySQL", icon: "🗄️" },
    { name: "Flask", icon: "🔥" },
  ];

  return (
    <div className="bg-black text-white font-sans scroll-smooth">
      {/* Header */}
      <header className="fixed top-0 w-full z-50 bg-black bg-opacity-80 text-white shadow-md">
        <nav className="max-w-6xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="text-xl font-bold">POLAGONI LOKESH PORTFOLIO</h1>
          <ul className="flex gap-6 text-sm">
            {["projects", "internships", "skills", "contact"].map((section) => (
              <li key={section} className="cursor-pointer capitalize hover:text-blue-400">
                <a href={`#${section}`}>{section}</a>
              </li>
            ))}
          </ul>
        </nav>
      </header>

      {/* Hero */}
      <section className="h-screen flex flex-col justify-center items-center text-center px-6 bg-cover bg-center relative" style={{ backgroundImage: `url('/assets/hero-bg.jpg')` }}>
        <div className="absolute inset-0 bg-black opacity-70 z-0"></div>
        <div className="relative z-10">
          <img src="/assets/profile.png" alt="Lokesh" className="w-36 h-36 rounded-full mb-6 border-4 border-blue-500 object-cover" />
          <h1 className="text-5xl font-bold text-blue-400">Polagoni Lokesh</h1>
          <p className="text-gray-300 mt-4">Cyber Security & Deep Learning Enthusiast</p>
        </div>
      </section>

      {/* Projects */}
      <Element name="projects">
        <section id="projects" className="py-20 px-6 text-center bg-black">
          <h2 className="text-4xl font-bold mb-12">Projects</h2>
          <div className="grid md:grid-cols-2 gap-10 max-w-6xl mx-auto">
            {projects.map((project, i) => (
              <motion.div
                key={i}
                className="bg-gray-900 border border-gray-700 rounded-xl p-6 text-left shadow-md"
                whileHover={{ scale: 1.02 }}
              >
                <img src={project.image} alt={project.title} className="rounded-md mb-4 h-48 w-full object-cover" />
                <h3 className="text-xl font-semibold text-white">{project.title}</h3>
                <p className="text-gray-400 mt-1 mb-4">{project.summary}</p>
                <button
                  className="bg-blue-500 text-white px-4 py-2 rounded"
                  onClick={() => setSelectedProject(project)}
                >
                  View Project
                </button>
              </motion.div>
            ))}
          </div>
        </section>
      </Element>

      {/* Internships */}
      <Element name="internships">
        <section id="internships" className="py-20 px-6 text-center bg-black">
          <h2 className="text-4xl font-bold mb-12">Internships</h2>
          <div className="grid md:grid-cols-1 gap-8 max-w-4xl mx-auto">
            {internships.map((intern, i) => (
              <motion.div
                key={i}
                className="bg-gray-900 border border-gray-700 rounded-xl p-6 text-left shadow-md"
                whileHover={{ scale: 1.02 }}
              >
                <h3 className="text-xl font-semibold text-white">{intern.title}</h3>
                <p className="text-gray-400">{intern.org} ({intern.duration})</p>
                <button
                  className="mt-4 bg-blue-500 text-white px-4 py-2 rounded"
                  onClick={() => setSelectedInternship(intern)}
                >
                  View Details
                </button>
              </motion.div>
            ))}
          </div>
        </section>
      </Element>

      {/* Skills */}
      <Element name="skills">
        <section id="skills" className="py-20 px-6 text-center bg-black">
          <h2 className="text-4xl font-bold mb-12">Skills</h2>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-8 max-w-4xl mx-auto">
            {skills.map((skill, i) => (
              <div key={i} className="bg-gray-800 text-white py-6 rounded-xl shadow-md">
                <div className="text-4xl mb-2">{skill.icon}</div>
                <div className="text-sm font-semibold">{skill.name}</div>
              </div>
            ))}
          </div>
        </section>
      </Element>

      {/* Contact */}
      <Element name="contact">
        <section id="contact" className="py-20 px-6 text-center bg-black">
          <h2 className="text-4xl font-bold mb-12">Contact</h2>
          <p className="text-lg text-gray-300">Email: lokesh@example.com</p>
          <p className="text-lg text-gray-300 mt-2">Phone: +91-9876543210</p>
          <p className="text-lg text-blue-400 mt-2">
            <a href="https://linkedin.com/in/polagoni-lokesh" target="_blank" rel="noopener noreferrer">
              LinkedIn Profile
            </a>
          </p>
        </section>
      </Element>

      {/* Modals */}
      <AnimatePresence>
        {selectedProject && (
          <motion.div
            className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center z-50"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedProject(null)}
          >
            <div className="bg-gray-800 p-6 rounded-xl max-w-lg w-full" onClick={(e) => e.stopPropagation()}>
              <h3 className="text-2xl font-bold text-blue-300 mb-4">{selectedProject.title}</h3>
              <p className="text-gray-300">{selectedProject.description}</p>
            </div>
          </motion.div>
        )}
        {selectedInternship && (
          <motion.div
            className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center z-50"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedInternship(null)}
          >
            <div className="bg-gray-800 p-6 rounded-xl max-w-lg w-full" onClick={(e) => e.stopPropagation()}>
              <h3 className="text-2xl font-bold text-blue-300 mb-4">{selectedInternship.title}</h3>
              <p className="text-gray-300">{selectedInternship.description}</p>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default App;
